import{_ as n,c as o,o as t,ae as i}from"./chunks/framework.AELpvxz7.js";const p=JSON.parse('{"title":"CHM+ Feature Introduction","description":"The best choice for smoothly reading CHM documents on iPhone and iPad.","frontmatter":{"title":"CHM+ Feature Introduction","description":"The best choice for smoothly reading CHM documents on iPhone and iPad."},"headers":[],"relativePath":"blog/index.md","filePath":"blog/index.md"}'),a={name:"blog/index.md"};function r(s,e,d,l,c,g){return t(),o("div",null,e[0]||(e[0]=[i('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h1><p><strong>CHM+</strong> is dedicated to providing you with an excellent reading experience for <code>CHM</code> (Compiled HTML Help) files on your <code>iPhone</code> and <code>iPad</code> devices. Whether you need to consult technical documentation, e-books, or help files, <strong>CHM+</strong> makes it easy.</p><h2 id="key-features" tabindex="-1">Key Features: <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features:&quot;">​</a></h2><ul><li><strong>Smooth Reading Experience</strong>: Optimized rendering engine ensures fast loading and smooth scrolling, handling even large CHM files with ease.</li><li><strong>Precise Content Search</strong>: Supports full-text search and index search to quickly locate the chapters and content you need.</li><li><strong>Convenient Bookmark Management</strong>: Easily add, delete, and manage bookmarks to quickly return to important information points.</li><li><strong>Multiple Encoding Support</strong>: Automatically detects or allows manual selection of file encoding to resolve common garbled text issues in CHM files.</li><li><strong>User-Friendly Interface</strong>: Clean and intuitive design provides a comfortable reading interface and convenient operation.</li><li><strong>File Management</strong>: Supports importing CHM files via the <code>Files</code> app or using the <code>Share</code> function to add them to the app for management.</li><li><strong>VIP Ad-Free</strong>: Offers a VIP option to remove in-app advertisements for an uninterrupted reading experience.</li></ul><p><strong>CHM+</strong>, making CHM reading simple and efficient.</p>',5)]))}const m=n(a,[["render",r]]);export{p as __pageData,m as default};
